mapper.select:
select
routes_id,depart_city,arrival_city,depart_date,arrival_date,plane_id,common_price,commerial_price,first_price
from t_routes


        and routes_id = #{routes.routesId}



        and depart_city like #{departCity}



        and arrival_city like #{arrivalCity}


        and depart_date >= #{routes.departDateStart}


        and depart_date <= #{routes.departDateEnd}


        and arrival_date >= #{routes.arrivalDateStart}


        and arrival_date <= #{routes.arrivalDateEnd}


        and plane_id = #{routes.planeId}


        and common_price = #{routes.commonPrice}


        and commerial_price = #{routes.commerialPrice}


        and first_price = #{routes.firstPrice}
