mapper.insert:
insert into t_cart(customer_id, goods_img, goods_id, goods_num, goods_name, goods_price, total)
values (#{cart.customerId}, #{cart.goodsImg}, #{cart.goodsId}, #{cart.goodsNum}, #{cart.goodsName},
        #{cart.goodsPrice}, #{cart.total});

mapper.update:
update t_cart
set goods_num=#{cart.goodsNum},total=#{cart.total}
where cart_id = #{cart.cartId} and customer_id =#{cart.customerId}
